import { Button, Col, Row, Container, Navbar} from 'react-bootstrap';
import AuthenticationModal from './components/AuthenticationModal';
import React, { useState } from 'react';
import Blog from './pages/Blog';
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  useParams
} from "react-router-dom";
import './App.css';



export default function App() {

  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [authentication, setAuthentication] = useState("Register");
  let { username } = useParams();

  return (

    <Container>


    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Welcome</Link>
            </li>
            <li>
              <Link to="/about">Login</Link>
            </li>
            <li>
              <Link to="/users">Register</Link>
            </li>
          </ul>
        </nav>

        {/* A <Switch> looks through its children <Route>s and
            renders the first one that matches the current URL. */}
        <Switch>
          <Route path="/about">
            <About />
          </Route>
          <Route path="/:username}">
            <Blog />
          </Route>
          <Route path="/">
            <Home email={email} setEmail={setEmail} password= {password} setPasswordd={setPassword} authentication={authentication} setAuthentication={setAuthentication}/>
          </Route>
        </Switch>
      </div>
    </Router>
    </Container>
  );
}

function Home({email, setEmail, password, setPassword, authentication, setAuthentication}) {
  const [show, setShow] = useState(false);


  function handleClose(){
      setShow(false)
  }

  function handleSubmit(e){
      // send to backend??
      console.log(email)
      console.log(password)

  }

  function handleClick(name){
      console.log(name)
      setAuthentication(name)
      setShow(true)
  }

  return (
    <Container>
  <Row className="justify-content-md-center">
        <Col className="text-center">
        <h1>
            Welcome to MyRead
        </h1>
        <p>
            Register or log in to get started.
        </p>
        <Button 
            variant="primary"
            name="Register"
            onClick={(e) => handleClick(e.currentTarget.name)}>
            Register
        </Button>{' '}
        <Button variant="primary"
            name="Log in"
            onClick={(e) => handleClick(e.currentTarget.name)}>
            Log in
        </Button>
        <AuthenticationModal email={email} setEmail={setEmail} password={password} setPassword={setPassword} show={show} handleClose={handleClose} handleSubmit={handleSubmit} type={authentication}></AuthenticationModal>
        </Col>
        <Navbar fixed="bottom" >
        <Container>
        Your own blog in seconds.
        </Container>
    </Navbar>
    </Row> 
    </Container>
)
}

function About() {
  return <h2>About</h2>;
}
